uint32_t pixelsR_[LIGHT_SIZE];
uint32_t pixelsG_[LIGHT_SIZE];
uint32_t pixelsB_[LIGHT_SIZE];

uint32_t errorsR_[LIGHT_SIZE];
uint32_t errorsG_[LIGHT_SIZE];
uint32_t errorsB_[LIGHT_SIZE];

PIO ditherPio;
int ditherSM;

static inline uint32_t correct(uint32_t val) {
  return cf[val >= maxChannelVal-1 ? maxChannelVal-1 : val];
}

static inline void put_pixel(uint32_t pixel_grb)
{
  pio_sm_put_blocking(ditherPio, ditherSM, pixel_grb << 8u);
}

static inline uint32_t urgb_u32(uint8_t r, uint8_t g, uint8_t b)
{
  return ((uint32_t)(r) << 8) |
         ((uint32_t)(g) << 16) |
         (uint32_t)(b);
}

static void ditherLoop()
{
  int valuer, valueg, valueb;
  int shift = bit_depth - 8;

  while (1)
  {
    for (int i = 0; i < LIGHT_SIZE; i++)
    {
      valueb = (pixelsB_[i] + errorsB_[i]) >> shift;
      valuer = (pixelsR_[i] + errorsR_[i]) >> shift;
      valueg = (pixelsG_[i] + errorsG_[i]) >> shift;
      put_pixel(urgb_u32(valuer, valueg, valueb));
      errorsB_[i] = (pixelsB_[i] + errorsB_[i]) -
                    (valueb << shift);
      errorsR_[i] = (pixelsR_[i] + errorsR_[i]) -
                    (valuer << shift);
      errorsG_[i] = (pixelsG_[i] + errorsG_[i]) -
                    (valueg << shift);
    }
    sleep_us(400);
  }
}

Light::Light(uint pin, PIO &pio, bool ditherOnCore1)
    : pin_(pin), pio_(pio), dither_(ditherOnCore1)
{
  for (uint32_t i = 0; i < LIGHT_SIZE; i++)
  {
    pixVals_[i] = 0;
  }
  printf("Created light with pin %d.\n", pin_);
}

bool Light::init()
{
  ws2812SM_ = pio_claim_unused_sm(pio_, true);
  if (ws2812SM_ == -1)
  {
    printf("Error: could not claim state machine for light.\n");
    return false;
  }

  if (!init_neopixel_program(pio_, &ws2812Offset_))
  {
    printf("Error: could not initialize Neopixel program.\n");
    return false;
  }

  ws2812_program_init(pio_, ws2812SM_, ws2812Offset_, pin_, 800000, IS_RGBW);
  printf("Started light on SM %d.\n", ws2812SM_);

  if (dither_)
  {
    ditherPio = pio_;
    ditherSM = ws2812SM_;
    multicore_launch_core1(ditherLoop);
    printf("Light dither loop started on core1.\n");
  }
  else
  {
    update();
  }

  return true;
}

void Light::put_pixel(uint32_t pixel_grb)
{
  pio_sm_put_blocking(pio_, ws2812SM_, pixel_grb << 8u);
}

void Light::setPixels(const PixelRange& range, const Color& color) {
  uint16_t pix = range.from;
  while (pix != range.to) {
    setPixel(pix, color);
    pix++;
    if (pix == LIGHT_SIZE) {
      if (range.to >= pix) {
        return;
      } else {
        pix = 0;
      }
    }
  }
}

void Light::setPixel(uint32_t pixelIndex, const Color& color) {
  setPixel(pixelIndex, color.r, color.g, color.b);
}

void Light::setPixel(uint32_t pixelIndex, uint32_t pixelGRB)
{
  if (dither_) {
    pixelsR_[pixelIndex] = correct((pixelGRB >> 8) & 0xff);
    pixelsG_[pixelIndex] = correct((pixelGRB >> 16) & 0xff);
    pixelsB_[pixelIndex] = correct(pixelGRB & 0xff);
  } else {
    pixVals_[pixelIndex] = pixelGRB;
  }
}

void Light::setPixel(uint32_t pixelIndex, uint32_t rVal, uint32_t gVal, uint32_t bVal)
{
  if (dither_)
  {
    pixelsR_[pixelIndex] = correct(rVal);
    pixelsG_[pixelIndex] = correct(gVal);
    pixelsB_[pixelIndex] = correct(bVal);
  }
  else
  {
    pixVals_[pixelIndex] = ((gVal & 0xff) << 16) | ((rVal & 0xff) << 8) | (bVal & 0xff);
  }
}

void Light::update()
{
  if (!dither_)
  {
    for (uint32_t i = 0; i < LIGHT_SIZE; i++)
    {
      uint32_t val = (pixVals_[i]);
      pio_sm_put_blocking(pio_, ws2812SM_, val << 8u);
    }
  }
}

void Light::setBrightnessFraction(float bf)
{
  brightnessFraction_ = bf;
  if (brightnessFraction_ > 1.0)
  {
    brightnessFraction_ = 1.0;
  }
  else if (brightnessFraction_ < 0.0)
  {
    brightnessFraction_ = 0.0;
  }
}
